mapper.select:
/*NoticeMapper.selectNotice Query*/ SELECT * FROM notice_board WHERE post_delete_yn = 'N'
AND post_id < #{postId}
ORDER BY post_id DESC LIMIT 10

mapper.update:
/*NoticeMapper.updateNotice Query*/
UPDATE post
   SET post_title = #{postTitle}
      ,post_update_date = #{postUpdateDate}
      ,post_content = #{postContent}
 WHERE post_id = #{postId}

mapper.delete:
UPDATE notice_board
SET post_title="삭제된 게시물 입니다." , post_content="삭제된 게시물입니다."
WHERE post_id = #{postId}

mapper.update:
UPDATE notice_board
SET post_hit = #{postHit}
WHERE post_id = #{postId}
